package 백준;
import java.util.*;
public class 질문_A더하기B빼기4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in=new Scanner(System.in);
		int a,b;
		
		while(in.hasNextInt()) {
			a=in.nextInt();
			b=in.nextInt();   
			System.out.println(a+b);
		}
	}

}
