package Object_String;

public class Math클래스 {
	//모든 메소드는 static타입-> 클래스 이름으로 바로 호출해야함!
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.PI);//파이
		System.out.println(Math.ceil(3.14));//올림(3.14->4)
		System.out.println(Math.floor(3.14));//내림
		System.out.println(Math.sqrt(4));//제곱근
		System.out.println(Math.exp(2));//e의 2승
		System.out.println(Math.round(3.14));//반올림
	}
}
